import React, { Component } from "react";
import { CardList } from "./components/card-list/card-list.component";
import "./App.css";
import { Card } from "./components/card/card.component";

class App extends Component {
  constructor() {
    super();
    this.state = {
      monsters: [],
      searchField: "",
      selectedMonstersIds: [],
      selectedMonsters: [],
    };
  }

  componentDidMount = () => {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then((response) => response.json())
      .then((users) => this.setState({ monsters: users }));
  };
  //   handleClick = (id) => {
  //     const monstersCopy = this.state.monsters;

  //     const selectedMonster = monstersCopy.filter((item) => {
  //       return item.id === id;
  //     });

  //     this.setState((prevState) => ({
  //       selectedMonsters: [...prevState.selectedMonsters, selectedMonster].flat(
  //         2
  //       ),
  //     }));
  //   };

  handleClick = (id) => {
    if (!this.state.selectedMonstersIds.includes(id)) {
      this.state.selectedMonstersIds.push(id);

      const selectedMonster = this.state.monsters.filter((item) => {
        return item.id === id;
      });

      this.setState((prevState) => ({
        selectedMonsters: [...prevState.selectedMonsters, selectedMonster].flat(
          2
        ),
      }));
    }
  };

  render() {
    const { monsters } = this.state;

    return (
      <div className="App">
        <div className="main">
          <div className="w-50">
            <h1>All Monsters</h1>
            <CardList handleClick={this.handleClick} monsters={monsters} />
          </div>

          <div className="w-50">
            <h1>
              Selected Monsters 
              {this.state.selectedMonsters.length > 0
                ? this.state.selectedMonsters.length 
                : ""}
            </h1>
            {this.state.selectedMonsters.length > 0
                ?  <CardList monsters={this.state.selectedMonsters} />
                : "You haven't selected any monster"}
            <div className="card-list">

            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
